static bool obj_read(objects_cache_t *ocp,
                     oc_object_t *objp,
                     bool async) {

  test_emit_token('a' + objp->obj_key);

  objp->obj_flags &= ~OC_FLAG_NOTSYNC;

  if (async) {
    chCacheReleaseObject(ocp, objp);
  }

  return false;
}

